import React, { Component } from 'react';
import logo from './logo.svg';
import {Videoplayer} from './components/videoplayer'
import './App.css';

class App extends Component {
  constructor(props){
    super(props);
    this.state={
      movie:[{
        name:'Kgf',
        hero:'Yash'
      },
      {
        name:'Prem Adda',
        hero:'Prem'
      },
      {
        name:'Gayathri',
        hero:'Madurai'
      },
      {
        name:'Kgf2',
        hero:'Yash'
      },
      {
        name:'Kgf3',
        hero:'Yash'
      },
      {
        name:'Kgf4',
        hero:'Yash'
      },
      {
        name:'Kgf5',
        hero:'Yash'
      },
      {
        name:'Kgf6',
        hero:'Yash'
      },
      {
        name:'Kgf7',
        hero:'Yash'
      },
    ],
    selectedmovie:-1
    }
  }
  render() {
    const  {movie,selectedmovie}=this.state;
    const movielist=movie.map((list,index)=>{
    if(selectedmovie===index)
      return(
        <div  className=" col-xs-6 col-sm-4 col-md-3 card">
        <div className="card-content">
        {list.name}
        </div>
        <Videoplayer/>
        </div>
      )
    })
    return (
      <div   className="App  container">
      <div className="row">
      {movielist}
      </div>
      </div>
    );
  }
}

export default App;
